contracts/interface/ISettUpgradeable.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.7.6;

import "../external/interfaces/badger/ISett.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

interface ISettUpgradeable is ISett, IERC20Upgradeable {
    function version() external view returns (string memory);

    /// @notice Return the total balance of the underlying token within the system
    /// @notice Sums the balance in the Sett, the Controller, and the Strategy
    function balance() external view returns (uint256);

    /// @notice Defines how much of the Setts' underlying can be borrowed by the Strategy for use
    /// @notice Custom logic in here for how much the vault allows to be borrowed
    /// @notice Sets minimum required on-hand to keep small withdrawals cheap
    function available() external view returns (uint256);
}

contracts/interface/IYieldSource.sol.content:
// SPDX-License-Identifier: MIT
/// @title Custom yield source interface
/// @dev 
///  - All deposits are denominated in the depositToken() ERC20.
///  - The balanceOfToken(address) function is similar to the Aave aToken, in that the balance must always increase.
/// @author https://github.com/pooltogether/yield-source-interface
pragma solidity >=0.4.0 <0.8.0;

/// @title Defines the functions used to interact with a yield source.  The Prize Pool inherits this contract.
/// @notice Prize Pools subclasses need to implement this interface so that yield can be generated.
interface IYieldSource {
    /// @notice Returns the ERC20 asset token used for deposits.
    /// @return The ERC20 asset token
    function depositToken() external view returns (address);

    /// @notice Returns the total balance (in asset tokens).  This includes the deposits and interest.
    /// @return The underlying balance of asset tokens
    function balanceOfToken(address addr) external returns (uint256);

    /// @notice Supplies tokens to the yield source.  Allows assets to be supplied on other user's behalf using the `to` param.
    /// @param amount The amount of `token()` to be supplied
    /// @param to The user whose balance will receive the tokens
    function supplyTokenTo(uint256 amount, address to) external;

    /// @notice Redeems tokens from the yield source.
    /// @param amount The amount of `token()` to withdraw.  Denominated in `token()` as above.
    /// @return The actual amount of tokens that were redeemed.
    function redeemToken(uint256 amount) external returns (uint256);
}

